/**
 * Options for re-framing multi-line text
 */
interface TextFrameOptions {
    /**
     * Tab expansion size (number of spaces per tab)
     * @default 8
     */
    tabsize?: number

    /**
     * Indentation level (number of spaces to add)
     * @default 0
     */
    indent?: number

    /**
     * Number of leading blank lines to add
     * @default 0
     */
    leading?: number

    /**
     * Number of trailing blank lines to add
     * @default 0
     */
    trailing?: number
}

/**
 * TextFrame function interface
 */
interface TextFrame {
    /**
     * Re-frame multi-line text (regular call)
     *
     * @param text - The text to re-frame
     * @param options - Optional configuration options
     * @returns Re-framed text
     */
    (text: string, ...options: TextFrameOptions[]): string

    /**
     * Re-frame multi-line text (tagged template literal)
     *
     * @param strings - Template literal strings
     * @param values - Template literal interpolated values
     * @returns Re-framed text
     */
    (strings: TemplateStringsArray, ...values: unknown[]): string

    /**
     * Re-frame multi-line text (factory method)
     *
     * @param options - Default configuration options
     * @returns Pre-configured textframe function
     */
    (options: TextFrameOptions): TextFrame

    /**
     * Raw functionality for re-framing multi-line text
     *
     * @param text - The text to re-frame
     * @param options - Optional configuration options
     * @returns Re-framed text
     */
    raw(text: string, options?: TextFrameOptions): string

    /**
     * Factory functionality for creating pre-configured textframe functions
     *
     * @param defaultOptions - Default configuration options
     * @returns Pre-configured textframe function
     */
    make(defaultOptions?: TextFrameOptions): TextFrame
}

/**
 * Re-frame multi-line text by removing common leading indentation,
 * expanding tabs, trimming whitespace, and optionally adding indentation
 *
 * @example
 * ```typescript
 * const textframe = require("textframe")
 *
 * // Regular call
 * let text = textframe(`
 *     foo
 *         bar
 *     baz
 * `)
 *
 * // Tagged template literal
 * let text2 = textframe`
 *     foo
 *         bar
 *     baz
 * `
 *
 * // With options
 * let text3 = textframe(`
 *     foo
 *         bar
 *     baz
 * `, { indent: 4 })
 *
 * // Factory method
 * const tf = textframe({ indent: 4 })
 * let text4 = tf`
 *     foo
 *         bar
 *     baz
 * `
 * ```
 */
declare const textframe: TextFrame

export = textframe

